import React from 'react';

class Login extends React.Component{
	constructor(){
		super();
		this.state = {
			isLoggedIn: false,
		};
	}

	onClick(){
		this.setState(() => {
			return{
				isLoggedIn: true
			};
		})
	}

	render(){
		return(
			<div>
				{this.state.isLoggedIn ? 
					<h3>You are logged in</h3> : <h3>You are logged out</h3>}
			</div>
			);
	}
}

export default Login;
