import React from 'react';

function TodoItem(props){
	const completedStyle = {
		fontStyle: "italic",
	};

	return(
		<div className="todo-item">
			<input 
				type="checkbox" 
				checked={props.item.completed}
				onChange={() => props.handleClick(props.item.id)}
			/>
			<p>{props.item.task}</p>
		</div>
		);
}

export default TodoItem;
